import React, { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";
import "../../style/styles.css";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux/es/exports";
import { cargarAranceles } from "../../actions";

export default function TablaBecas({ ciclo }) {
  const dispatch = useDispatch();
  const navigation = useNavigate();
  const aranceles = useSelector((state) => state.aranceles);
  const [order, setOrder] = useState("ASC");

  async function cargarTabla() {
    document.querySelector(".tr-loader").classList.remove("d-none");
    await axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/aranceles/traercuotas.php?&ciclo=" +
          ciclo
      )
      .then((response) => {
        document.querySelector(".tr-loader").classList.add("d-none");
        return dispatch(cargarAranceles(response.data));
      });
  }

  useEffect(() => {
    dispatch(cargarAranceles([]));
    cargarTabla();
  }, [ciclo]);

  function orden(e, col) {
    e.preventDefault();

    let i = col === "codigo" ? 1 : 0;

    if (document.querySelector(`.${col}`).innerHTML.slice(-1) === "↑") {
      if (col === "codigo") {
        document.querySelector(`.${col}`).innerHTML = `Código ↓`;
      } else if (col === "nombre") {
        document.querySelector(`.${col}`).innerHTML = `Nombre ↓`;
      }
    } else {
      if (col === "codigo") {
        document.querySelector(`.${col}`).innerHTML = `Código ↑`;
      } else if (col === "nombre") {
        document.querySelector(`.${col}`).innerHTML = `Nombre ↑`;
      }
    }

    if (order === "ASC") {
      const sorted = [...aranceles].sort((a, b) => (a[i] > b[i] ? 1 : -1));
      dispatch(cargarAranceles(sorted));
      setOrder("DESC");
    }

    if (order === "DESC") {
      const sorted = [...aranceles].sort((a, b) => (a[i] < b[i] ? 1 : -1));
      dispatch(cargarAranceles(sorted));
      setOrder("ASC");
    }
  }

  return (
    <div style={{ height: "900px" }} className="table-responsive">
      <table className="w-100 table-condensed table-hover table-bordered text-center table border-top border-secondary">
        <thead className="py-5 header">
          <tr className="bg-light" style={{ cursor: "pointer" }}>
            <th
              onClick={(e) => orden(e, "descripcion")}
              className="col-1 text-nowrap descripcion"
            >
              Descripción ↑
            </th>
            <th
              onClick={(e) => orden(e, "tipo")}
              className="col-1 text-nowrap tipo"
            >
              Tipo ↑
            </th>
            <th
              onClick={(e) => orden(e, "fecha1")}
              className="col-1 text-nowrap fecha1"
            >
              Fecha 1er Vto. ↑
            </th>
            <th
              onClick={(e) => orden(e, "imp1")}
              className="col-1 text-nowrap imp1"
            >
              Imp. 1er Vto. ↑
            </th>
            <th
              onClick={(e) => orden(e, "fecha2")}
              className="col-1 text-nowrap fecha2"
            >
              Fecha 2do Vto. ↑
            </th>
            <th
              onClick={(e) => orden(e, "imp2")}
              className="col-1 text-nowrap imp2"
            >
              Imp. 2do Vto. ↑
            </th>
            <th
              onClick={(e) => orden(e, "arancel")}
              className="col-1 text-nowrap arancel"
            >
              Arancel ↑
            </th>
            <th className="col-1 text-nowrap">Editar</th>
            <th className="col-1 text-nowrap">Clonar</th>
            <th className="col-1 text-nowrap">Eliminar</th>
          </tr>
        </thead>
        <tbody>
          {aranceles &&
            aranceles.map((a) => (
              <tr
                style={{ cursor: "pointer" }}
                key={a.idcuota}
                onClick={(e) => {
                  document
                    .querySelector(".navbar")
                    .classList.remove("active-nav");
                  document
                    .querySelector(".contenedor")
                    .classList.remove("active-contenedor");
                  navigation(`/aranceles/${a.idcuota}`);
                }}
              >
                <td className="text-left">
                  <div className="">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold text-nowrap "
                    >
                      {a.descripcion}
                    </p>
                  </div>
                </td>
                <td className="align-middle">
                  <div className="">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold text-nowrap "
                    >
                      {a.tipo}
                    </p>
                  </div>
                </td>
                <td className="align-middle">
                  <div className="">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold text-nowrap "
                    >
                      {a.fecha1}
                    </p>
                  </div>
                </td>
              </tr>
            ))}
          <tr>
            <td colSpan="4">
              <div>
                {aranceles === [] || !aranceles ? (
                  <div className="footer text-center">
                    No se registra información
                  </div>
                ) : (
                  ""
                )}
              </div>
            </td>
          </tr>
        </tbody>
      </table>
      <div id="tr-loader" className="d-none tr-loader">
        <div colSpan="7">
          <div
            className="spinner-border text-primary"
            style={{ width: "3rem", height: "3rem" }}
            role="status"
          ></div>
        </div>
      </div>
    </div>
  );
}
