import axios from "axios";
import React, { useState } from "react";
import "../../style/styles.css";

export default function Parametros() {
  const [idopcion, setIdOpcion] = useState();

  function traerDatosParametros() {
    axios.get(
      "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/usuarios/traerdatosparametros.php?idopcion=" +
        idopcion
    );
  }

  useEffect(() => {
    traerDatosParametros();
  }, [idopcion]);

  return (
    <div className="px-3 pt-3 pb-5 px-lg-5 mx-lg-5 pb-lg-0">
      <div className="pt-5">
        <div>
          <h4 className="">Parametros</h4>
          <p className="text-secondary">Envío de mails.</p>
        </div>
        <hr className="w-100 mx-0" />
        <div className="col col-lg-6 p-0">
          <div className="d-flex flex-md-row flex-row">
            <label
              style={{ fontSize: "13px" }}
              className=" align-middle col-sm-2 control-label text-label p-0 m-0"
            >
              Opción
            </label>
            <select
              className="form-control form-control-sm mb-2 mb-sm-0 col-3 col-sm-2 col-lg-2 col-xl-3 mr-3"
              onChange={(e) => setIdOpcion(e.target.value)}
            >
              <option value="1">ENVIO DE AVISOS</option>
              <option value="2">NOTIFICACION DEUDA</option>
            </select>
          </div>
        </div>
      </div>
      <hr className="w-100 mx-0" />
      <label
        style={{ fontSize: "13px" }}
        className=" align-middle col-sm-2 control-label text-label p-0 m-0"
      >
        Texto mail envío
      </label>
      <hr className="w-100 mx-0" />
      <button className="btn btn-sm btn-primary">Grabar</button>
    </div>
  );
}
