import React, { useEffect, useMemo } from "react";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import { useTable, useSortBy, useBlockLayout } from "react-table";
import mail from "../../utils/mail.png";
import printer from "../../utils/printer.png";
import { useSticky } from "react-table-sticky";
import styled from "styled-components";

export default function TablaLiquidacion({
  ciclo,
  idCuota,
  nombreCuota,
  idAlumno,
}) {
  const dispatch = useDispatch();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          idCuota +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        if (response.data) {
          return dispatch(cargarTablaLiquidaciones(response.data));
        } else {
          return dispatch(cargarTablaLiquidaciones([]));
        }
      });
  }

  const Styles = styled.div`
    .table {
      border: 1px solid #ddd;
      display: block !important;

      .tr {
        :last-child {
          .td {
            border-bottom: 0;
          }
        }
      }

      .th,
      .td {
        padding: 5px;
        border-bottom: 1px solid #ddd;
        border-right: 1px solid #ddd;
        background-color: #fff;
        overflow: hidden;

        :last-child {
          border-right: 0;
        }
      }

      &.sticky {
        overflow: scroll;
        .header,
        .footer {
          position: sticky;
          z-index: 1;
        }

        .header {
          top: 0;
          box-shadow: 0px 3px 3px #ccc;
        }

        .footer {
          bottom: 0;
          box-shadow: 0px -3px 3px #ccc;
        }

        .body {
          position: relative;
          z-index: 0;
        }

        [data-sticky-td] {
          position: sticky;
        }

        [data-sticky-last-left-td] {
          box-shadow: 2px 0px 3px #ccc;
        }

        [data-sticky-first-right-td] {
          box-shadow: -2px 0px 3px #ccc;
        }
      }
    }
  `;

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  const columns = useMemo(
    () => [
      {
        Header: "Alumno",
        accessor: "alumno",
        sticky: "top",
      },
      {
        Header: "Nivel",
        accessor: "nivel",
        sticky: "top",
      },
      {
        Header: "Curso",
        accessor: "nombre",
        sticky: "top",
      },
      {
        Header: "Fecha Liquidación",
        accessor: "fhliq",
        sticky: "top",
      },
      {
        Header: "Acción",
        accessor: "traspaso",
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            style={{ width: "65px" }}
            className={
              cell.value === "0"
                ? "btn rounded btn-success d-flex justify-content-center"
                : "btn rounded btn-danger d-flex justify-content-center"
            }
            value={cell}
            onClick={(e) => handleClick(e, cell)}
          >
            {cell.value === "0" ? "Liquidar" : "Revertir"}
          </button>
        ),
      },
      {
        Header: "Imprimir",
        accessor: "",
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className="d-flex justify-content-center border-0 bg-transparent"
            onClick={(e) => imprimirLiquidaciones(e, cell)}
          >
            <img src={printer} alt=""></img>
          </button>
        ),
      },
      {
        Header: "Enviar Correo",
        accessor: "",
        Cell: ({ cell }) => (
          <button
            className="d-flex justify-content-center border-0 bg-transparent"
            onClick={(e) => enviarLiquidaciones(e, cell)}
          >
            <img src={mail} alt=""></img>
          </button>
        ),
      },
    ],
    []
  );

  function handleClick(e, cell) {
    e.preventDefault();
    if (cell.row.original.traspaso === "1") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/revertircuota.php?ciclo=" +
            ciclo +
            "&idcuota=" +
            idCuota +
            "&idcurso=" +
            cell.row.original.idcurso +
            "&idnivel=" +
            cell.row.original.idnivel +
            "&idalumno=" +
            cell.row.original.idalumno
        )
        .then((response) =>
          Swal.fire({
            title: "Error al Revertir Liquidación!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          })
        );
    } else {
      axios.post(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/liquidarcuota.php?" +
          "idcuota=" +
          idCuota +
          "&idcurso=" +
          cell.row.original.idcurso +
          "&userid=1" +
          "&idalumno=" +
          cell.row.original.idalumno
      );
    }
  }

  function imprimirLiquidaciones(e, cell) {
    e.preventDefault();
    window.open(
      "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/imprimir_boletapago.php?" +
        "idcuota=" +
        idCuota +
        "&idalum=" +
        cell.row.original.idalumno
    );
  }

  function enviarLiquidaciones(e, cell) {
    e.preventDefault();
    axios
      .post(
        "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/enviar_boletapagoweb.php?idcuota=" +
          idCuota +
          "&idalum=" +
          cell.row.original.idalumno +
          "&cuota=" +
          nombreCuota
      )
      .then((response) => {
        console.log(response.data);

        if (response.data.toString() === "") {
          Swal.fire({
            title: "El Correo ha sido enviado con EXITO",
            icon: "success",
            showCloseButton: true,
          });
        } else {
          Swal.fire({
            title: "Error al Enviar el Correo!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          });
        }
      });
  }

  const tableInstance = useTable(
    { columns, data: liquidaciones },
    useSortBy,
    useBlockLayout,
    useSticky
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    tableInstance;

  return (
    <Styles>
      <div
        {...getTableProps()}
        className="table sticky"
        style={{ height: 900 }}
      >
        <div className="header">
          {headerGroups.map((headerGroup) => (
            <div {...headerGroup.getHeaderGroupProps()} className="tr">
              {headerGroup.headers.map((column) => (
                <div {...column.getHeaderProps()} className="th">
                  {column.render("Header")}
                </div>
              ))}
            </div>
          ))}
        </div>
        <div {...getTableBodyProps()} className="body">
          {rows.map((row) => {
            prepareRow(row);
            return (
              <div {...row.getRowProps()} className="tr">
                {row.cells.map((cell) => (
                  <div {...cell.getCellProps()} className="td">
                    {cell.render("Cell")}
                  </div>
                ))}
              </div>
            );
          })}
        </div>
      </div>
    </Styles>
  );
}
