import React, { useEffect } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";

export default function TablaLiquidacion({ ciclo, idCuota, idAlumno }) {
  const dispatch = useDispatch();
  const navigation = useNavigate();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    console.log(ciclo, idCuota, idAlumno);
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          idCuota +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        return dispatch(cargarTablaLiquidaciones(response.data));
      });
  }

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  return (
    <div
      style={{ height: "900px" }}
      className="overflow-scroll table-responsive"
    >
      <table className="w-100 table-striped table-hover table-condensed">
        <thead className="bg-transparent">
          <tr>
            <th className="col">Alumno</th>
            <th className="col">Nivel</th>
            <th className="col">Curso</th>
            <th className="col">Fecha Liquidación</th>
            <th className="col">Acción</th>
            <th className="col">Imprimir Boleta</th>
            <th className="col">Enviar Correo</th>
          </tr>
        </thead>
        <tbody>
          {liquidaciones &&
            liquidaciones.map((u) => (
              <tr key={u[0]}>
                <td>
                  <div className="ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"alumno"}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"nivel"}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"curso"}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"fecha"}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"accion"}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"imprimir"}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {"enviar correo"}
                    </p>
                  </div>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}
