                  if (
                    e.code === "Minus" &&
                    ((e.code >= "48" && e.code <= "57") ||
                      (e.code >= "96" && e.code <= "105"))
                  ) {
                    e.preventDefault();
                  }
                }}
                className="form-control form-control-sm col-sm-4 col-lg-3 p-0 m-0 text-right"
                onChange={(e) => setImporte(e.target.value)}
              />
