// let orden = 1;
const initialState = {
  users: [],
  users2: [],
  user: "",
  materiasVinculadas: [],
  materias: [],
  materias2: [],
  materia: "",
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "CARGAR_DOCENTES":
      return {
        ...state,
        users: action.payload,
      };
    case "CARGAR_DOCENTES2":
      return {
        ...state,
        users2: action.payload,
      };
    case "CARGAR_DOCENTE":
      return {
        ...state,
        user: state.users.filter((u) => u[4] === action.payload),
      };

    case "BORRAR_DOCENTE":
      return {
        ...state,
        users: state.users.filter((u) => u[4] !== action.payload),
      };

    case "MODIFICAR_DOCENTE":
      const docente = action.payload;
      console.log(docente);
      return {
        ...state,
        users: state.users.map((u) => (u[4] === docente[4] ? docente : u)),
        users2: state.users.map((u) => (u[4] === docente[4] ? docente : u)),
      };

    case "BUSCAR_DOCENTE":
      return {
        ...state,
        user: state.users.filter((u) => u[4] === action.payload),
      };

    case "CARGAR_MATERIASVINCULADAS":
      return {
        ...state,
        materiasVinculadas: action.payload,
      };

    case "CARGAR_MATERIAS":
      return {
        ...state,
        materias: action.payload,
      };
    case "BORRAR_MATERIA":
      return {
        ...state,
        materias: state.materias.filter((m) => m[4] !== action.payload),
      };

    case "CARGAR_MATERIA":
      return {
        ...state,
        materia: state.materias.filter((u) => u[4] === action.payload),
      };

    case "MODIFICAR_MATERIA":
      const materia = action.payload;
      return {
        ...state,
        materias: state.materias.map((u) =>
          u[4] === materia[4] ? materia : u
        ),
        materias2: state.materias.map((u) =>
          u[4] === materia[4] ? materia : u
        ),
      };

    default:
      return { ...state };
  }
}
