import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";
import { traerDocentes } from "../actions";
import axios from "axios";

export default function Table() {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);

  // useEffect(() => {
  //   dispatch(traerDocentes());
  // }, [dispatch]);

  let json = async function getDocs() {
    return await axios.get(
      "https://www.califcolegios.wnpower.host/ipsaj/admin/docentes/div_lista_todos.php"
    );
  };
  console.log(json);

  return (
    <table className="table align-middle justify-content-center w-50 mx-auto bg-white">
      <thead className="bg-light">
        <tr>
          <th className="">Apellido</th>
          <th className="">Nombre</th>
          <th className="">Documento</th>
          <th className="">Email</th>
          <th className="">Accion</th>
        </tr>
      </thead>
      <tbody>
        {users &&
          users.map((u) => (
            <tr>
              <td>
                <div className="d-flex align-items-center">
                  <p className="fw-bold mb-1">{u.apellido}</p>
                </div>
              </td>
              <td>
                <div className="d-flex align-items-center">
                  <p className="fw-bold mb-1">{u.nombres}</p>
                </div>
              </td>
              <td>
                <div className="d-flex align-items-center">
                  <p className="fw-bold mb-1">{u.docnro}</p>
                </div>
              </td>
              <td>
                <div className="d-flex align-items-center">
                  <p className="fw-bold mb-1">{u.email}</p>
                </div>
              </td>
              <td>
                <NavLink
                  to={"/docente/" + u.iddocente}
                  type="button"
                  className="btn btn-link btn-sm btn-rounded"
                  id={u.iddocente}
                  edit="true"
                >
                  Edit
                </NavLink>
              </td>
            </tr>
          ))}
      </tbody>
    </table>
  );
}
