import React, { useEffect, useMemo } from "react";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import {
  useTable,
  useSortBy,
  useBlockLayout,
  useResizeColumns,
} from "react-table";
import mail from "../../utils/mail.png";
import printer from "../../utils/printer.png";
import { useSticky } from "react-table-sticky";
import styled from "styled-components";
import BTable from "react-bootstrap/Table";

export default function TablaLiquidacion({
  ciclo,
  idCuota,
  nombreCuota,
  idAlumno,
}) {
  const dispatch = useDispatch();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          (idCuota ? idCuota : 0) +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        if (response.data) {
          return dispatch(cargarTablaLiquidaciones(response.data));
        } else {
          return dispatch(cargarTablaLiquidaciones([]));
        }
      });
  }

  const Styles = styled.div`
    .table {
      border: 1px solid #ddd;
      font-size: 13px;
      overflow-x: scroll !important;

      .tr {
        :last-child {
          .td {
            border-bottom: 0;
          }
        }
      }

      .th {
        font-weight: bold;
      }

      .th,
      .td {
        padding: 5px;
        border-bottom: 1px solid #ddd;
        border-right: 1px solid #ddd;
        overflow: hidden;

        position: relative;
        :last-child {
          border-right: 0;
        }
      }

      .resizer {
        display: inline-block;
        width: 5px;
        height: 100%;
        position: absolute;
        right: 0;
        top: 0;
        transform: translateX(50%);
        z-index: 1;
        ${"" /* prevents from scrolling while dragging on touch devices */}
        touch-action:none;

        &.isResizing {
          background: black;
        }
      }

      &.sticky {
        overflow: scroll;
        .header,
        .footer {
          position: sticky;
          z-index: 1;
        }

        .header {
          top: 0;
          box-shadow: 0px 3px 3px #ccc;
        }

        .footer {
          bottom: 0;
          box-shadow: 0px -3px 3px #ccc;
        }

        .body {
          position: relative;
          z-index: 0;
        }

        [data-sticky-td] {
          position: sticky;
        }

        [data-sticky-last-left-td] {
          box-shadow: 2px 0px 3px #ccc;
        }

        [data-sticky-first-right-td] {
          box-shadow: -2px 0px 3px #ccc;
        }
      }
      .btn {
        font-size: 12px;
      }
    }
  `;

  useEffect(() => {
    cargarTabla();
    document.querySelector(".th").removeAttribute("title");
  }, [ciclo, idCuota, idAlumno]);

  const columns = useMemo(
    () => [
      {
        Header: "Alumno",
        accessor: "alumno",
        sticky: "top",
        Cell: ({ value }) => <div style={{ textAlign: "left" }}>{value}</div>,
      },
      {
        Header: "Nivel",
        accessor: "nivel",
        sticky: "top",
      },
      {
        Header: "Curso",
        accessor: "nombre",
        sticky: "top",
      },
      {
        Header: "Fecha Liquidación",
        accessor: "fhliq",
        sticky: "top",
      },
      {
        Header: "Acción",
        accessor: "traspaso",
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0"
                ? "btn rounded btn-success"
                : "btn rounded btn-danger"
            }
            value={cell}
            onClick={(e) => handleClick(e, cell)}
          >
            {cell.value === "0" ? "Liquidar" : "Revertir"}
          </button>
        ),
      },
      {
        Header: "Imprimir",
        accessor: "imprimir",
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0" ? "d-none" : "border-0 bg-transparent"
            }
            onClick={(e) => imprimirLiquidaciones(e, cell)}
          >
            <img src={printer} alt=""></img>
          </button>
        ),
      },
      {
        Header: "Enviar Correo",
        accessor: "enviar",
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0" ? "d-none" : "border-0 bg-transparent"
            }
            onClick={(e) => {
              enviarLiquidaciones(e, cell);
            }}
          >
            <img src={mail} alt=""></img>
          </button>
        ),
      },
    ],
    [ciclo, idCuota, idAlumno]
  );

  const defaultColumn = useMemo(
    () => ({
      minWidth: 100,
      width: window.screen.width < 1367 ? 167.4 : 221,
      maxWidth: 250,
    }),
    []
  );
  function handleClick(e, cell) {
    e.preventDefault();
    if (cell.row.original.traspaso === "1") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/revertircuota.php?ciclo=" +
            ciclo +
            "&idcuota=" +
            idCuota +
            "&idcurso=" +
            cell.row.original.idcurso +
            "&idnivel=" +
            cell.row.original.idnivel +
            "&idalumno=" +
            cell.row.original.idalumno
        )
        .then((response) => {
          if (response.data !== "") {
            Swal.fire({
              title: "Error al Revertir Liquidación!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            });
          }
        });
    } else {
      axios.post(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/liquidarcuota.php?" +
          "idcuota=" +
          idCuota +
          "&idcurso=" +
          cell.row.original.idcurso +
          "&userid=1" +
          "&idalumno=" +
          cell.row.original.idalumno
      );
      cargarTabla();
    }
    cargarTabla();
  }

  function imprimirLiquidaciones(e, cell) {
    e.preventDefault();
    window.open(
      "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/imprimir_boletapago.php?" +
        "idcuota=" +
        idCuota +
        "&idalum=" +
        cell.row.original.idalumno
    );
  }

  function enviarLiquidaciones(e, cell) {
    e.preventDefault();
    axios
      .post(
        "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/enviar_boletapagoweb.php?idcuota=" +
          idCuota +
          "&idalum=" +
          cell.row.original.idalumno +
          "&cuota=" +
          nombreCuota
      )
      .then((response) => {
        console.log(response.data);

        if (response.data.toString() === "") {
          Swal.fire({
            title: "El Correo ha sido enviado con EXITO",
            icon: "success",
            showCloseButton: true,
          });
        } else {
          Swal.fire({
            title: "Error al Enviar el Correo!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          });
        }
      });
  }

  const tableInstance = useTable(
    { columns, data: liquidaciones, defaultColumn },
    useSortBy,
    useBlockLayout,
    useResizeColumns,
    useSticky
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    state,
    resetResizing,
  } = tableInstance;

  return (
    <Styles>
      <BTable
        striped
        bordered
        hover
        responsive
        {...getTableProps()}
        className="table sticky text-center"
      >
        <div className="header">
          {headerGroups.map((headerGroup) => (
            <div {...headerGroup.getHeaderGroupProps()} className="tr">
              {headerGroup.headers.map((column) => (
                <div
                  {...column.getHeaderProps(
                    column.getSortByToggleProps({ title: "Cambiar Orden" })
                  )}
                  className="th"
                >
                  {column.render("Header")}
                  <span>
                    {column.isSorted ? (column.isSortedDesc ? " ↓" : " ↑") : ""}
                  </span>
                  <div
                    {...column.getResizerProps()}
                    className={`resizer ${
                      column.isResizing ? "isResizing" : ""
                    }`}
                  />
                </div>
              ))}
            </div>
          ))}
        </div>
        <tbody {...getTableBodyProps()} className="body">
          {rows.map((row) => {
            prepareRow(row);
            return (
              <div {...row.getRowProps()} className="tr">
                {row.cells.map((cell) => (
                  <div {...cell.getCellProps()} className="td">
                    {cell.render("Cell")}
                  </div>
                ))}
              </div>
            );
          })}
        </tbody>
      </BTable>
    </Styles>
  );
}
