import React, { useEffect, useMemo } from "react";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import { NavLink } from "react-router-dom";
import printer from "../../utils/printer.png";
import mail from "../../utils/mail.png";

export default function TablaLiquidacion({
  ciclo,
  idCuota,
  nombreCuota,
  idAlumno,
}) {
  const dispatch = useDispatch();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          (idCuota ? idCuota : 0) +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        if (response.data) {
          return dispatch(cargarTablaLiquidaciones(response.data));
        } else {
          return dispatch(cargarTablaLiquidaciones([]));
        }
      });
  }

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  const columns = useMemo(
    () => [
      {
        Header: "Alumno",
        accessor: "alumno",
        minWidth: 150,
        width: 265,
        maxWidth: 345,
        sticky: "top",
        Cell: ({ value }) => (
          <div
            style={{
              textAlign: "left",
              overflow: "hidden",
              whiteSpace: "nowrap",
            }}
          >
            {value}
          </div>
        ),
      },
      {
        Header: "Nivel",
        accessor: "nivel",
        minWidth: 100,
        width: 150,
        maxWidth: 229,
        sticky: "top",
      },
      {
        Header: "Curso",
        accessor: "nombre",
        minWidth: 100,
        width: 150,
        maxWidth: 229,
        sticky: "top",
      },
      {
        Header: "Fecha Liquidación",
        accessor: "fhliq",
        minWidth: 100,
        width: 150,
        maxWidth: 229,
        sticky: "top",
      },
      {
        Header: "Acción",
        accessor: "traspaso",
        minWidth: 100,
        width: 150,
        maxWidth: 229,
        sticky: "top",
        Cell: ({ cell }) => (
          <div>
            <button
              className={
                cell.value === "0"
                  ? "btn rounded btn-success btn-sm"
                  : "btn rounded btn-danger btn-sm"
              }
              value={cell}
              onClick={(e) => {
                handleClick(e, cell);
              }}
            >
              {cell.value === "0" ? "Liquidar" : "Revertir"}
            </button>
            <div
              id={"spinner " + cell.row.id}
              className="ml-3 mt-2 spinner-border spinner-border-sm spinner position-absolute d-none"
              role="status"
            >
              <span class="visually-hidden"></span>
            </div>
          </div>
        ),
      },
      {
        Header: "Imprimir",
        accessor: "imprimir",
        minWidth: 100,
        width: 150,
        maxWidth: 229,
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0" ? "d-none" : "border-0 bg-transparent"
            }
            onClick={(e) => imprimirLiquidaciones(e, cell)}
          >
            <img width={25} alt=""></img>
          </button>
        ),
      },
      {
        Header: "Enviar Correo",
        accessor: "enviar",
        minWidth: 100,
        width: 150,
        maxWidth: 229,
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0" ? "d-none" : "border-0 bg-transparent"
            }
            onClick={(e) => {
              enviarLiquidaciones(e, cell);
            }}
          >
            <img width={25} alt=""></img>
          </button>
        ),
      },
    ],
    [ciclo, idCuota, idAlumno]
  );

  function handleClick(e, cell) {
    e.preventDefault();
    document
      .getElementById(`spinner ${cell.row.id}`)
      .classList.remove("d-none");
    console.log(document.getElementById(`${cell.row.id}`));
    if (cell.row.original.traspaso === "1") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/revertircuota.php?ciclo=" +
            ciclo +
            "&idcuota=" +
            idCuota +
            "&idcurso=" +
            cell.row.original.idcurso +
            "&idnivel=" +
            cell.row.original.idnivel +
            "&idalumno=" +
            cell.row.original.idalumno
        )
        .then((response) => {
          if (response.data !== "") {
            Swal.fire({
              title: "Error al Revertir Liquidación!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            });
          }
        });
    } else {
      axios.post(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/liquidarcuota.php?" +
          "idcuota=" +
          idCuota +
          "&idcurso=" +
          cell.row.original.idcurso +
          "&userid=1" +
          "&idalumno=" +
          cell.row.original.idalumno
      );
      cargarTabla();
    }
    cargarTabla();
  }

  function imprimirLiquidaciones(e, l) {
    e.preventDefault();
    window.open(
      "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/imprimir_boletapago.php?" +
        "idcuota=" +
        idCuota +
        "&idalum=" +
        l.idalumno
    );
  }

  function enviarLiquidaciones(e, l) {
    e.preventDefault();
    axios
      .post(
        "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/enviar_boletapagoweb.php?idcuota=" +
          idCuota +
          "&idalum=" +
          l.idalumno +
          "&cuota=" +
          nombreCuota
      )
      .then((response) => {
        console.log(response?.data);

        if (response?.data?.toString() === "") {
          Swal.fire({
            title: "El Correo ha sido enviado con EXITO",
            icon: "success",
            html: `${response.data}`,
            showCloseButton: true,
          });
        } else {
          Swal.fire({
            title: "Error al Enviar el Correo!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          });
        }
      });
  }

  console.log(liquidaciones);
  return (
    <div
      style={{ height: "900px" }}
      className="overflow-scroll table-responsive"
    >
      <table className="w-100 table-striped table-hover table-condensed">
        <thead className="bg-transparent">
          <tr>
            <th className="col-6">Alumno</th>
            <th className="col">Nivel</th>
            <th className="col">Curso</th>
            <th className="col">Fecha Liquidación</th>
            <th className="col">Acción</th>
            <th className="col">Imprimir</th>
            <th className="col">Enviar Correo</th>
          </tr>
        </thead>
        <tbody>
          {liquidaciones &&
            liquidaciones.map((l) => (
              <tr style={{ cursor: "pointer" }} key={l[2]}>
                <td>
                  <div className="ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l.alumno}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l.nivel}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l.nombre}
                    </p>
                  </div>
                </td>

                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l.fhliq}
                    </p>
                  </div>
                </td>

                <td>
                  <div>
                    <button
                      className={
                        l.traspaso === "0"
                          ? "btn rounded btn-success btn-sm"
                          : "btn rounded btn-danger btn-sm"
                      }
                      onClick={(e) => {
                        handleClick(e);
                      }}
                    >
                      {l.traspaso === "0" ? "Liquidar" : "Revertir"}
                    </button>
                    <div
                      id={"spinner "}
                      className="ml-3 mt-2 spinner-border spinner-border-sm spinner position-absolute d-none"
                      role="status"
                    >
                      <span class="visually-hidden"></span>
                    </div>
                  </div>
                </td>

                <td>
                  <button
                    className={
                      l.traspaso === "0" ? "d-none" : "border-0 bg-transparent"
                    }
                    onClick={(e) => imprimirLiquidaciones(e, l)}
                  >
                    <img width={25} src={printer} alt=""></img>
                  </button>
                </td>

                <td>
                  <button
                    className={
                      l.traspaso === "0" ? "d-none" : "border-0 bg-transparent"
                    }
                    onClick={(e) => {
                      enviarLiquidaciones(e, l);
                    }}
                  >
                    <img width={25} src={mail} alt=""></img>
                  </button>
                </td>
              </tr>
            ))}
          <div>
            {liquidaciones.length === 0 ? (
              <div className="footer">No se registra información</div>
            ) : (
              ""
            )}
          </div>
        </tbody>
      </table>
    </div>
  );
}
