import React, { useEffect, useMemo } from "react";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import { NavLink } from "react-router-dom";
import printer from "../../utils/printer.png";
import mail from "../../utils/mail.png";
import Table from "react-bootstrap/Table";
import { useState } from "react";

export default function TablaLiquidacion({
  ciclo,
  idCuota,
  nombreCuota,
  idAlumno,
}) {
  const dispatch = useDispatch();
  const liquidaciones = useSelector((state) => state.liquidaciones);
  const [order, setOrder] = useState("ASC");

  function cargarTabla() {
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          (idCuota ? idCuota : 0) +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        if (response.data) {
          return dispatch(cargarTablaLiquidaciones(response.data));
        } else {
          return dispatch(cargarTablaLiquidaciones([]));
        }
      });
  }

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  function handleClick(e, l) {
    e.preventDefault();
    document.getElementById(`spinner ${l.idalumno}`).classList.remove("d-none");
    if (l.traspaso === "1") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/revertircuota.php?ciclo=" +
            ciclo +
            "&idcuota=" +
            idCuota +
            "&idcurso=" +
            l.idcurso +
            "&idnivel=" +
            l.idnivel +
            "&idalumno=" +
            l.idalumno
        )
        .then((response) => {
          if (response.data !== "") {
            Swal.fire({
              title: "Error al Revertir LiquidaciÃ³n!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            });
          }
        });
    } else {
      axios.post(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/liquidarcuota.php?" +
          "idcuota=" +
          idCuota +
          "&idcurso=" +
          l.idcurso +
          "&userid=1" +
          "&idalumno=" +
          l.idalumno
      );
      cargarTabla();
    }
    setTimeout(() => {
      document.getElementById(`spinner ${l.idalumno}`).classList.add("d-none");
      cargarTabla();
    }, 800);
  }

  function imprimirLiquidaciones(e, l) {
    e.preventDefault();
    window.open(
      "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/imprimir_boletapago.php?" +
        "idcuota=" +
        idCuota +
        "&idalum=" +
        l.idalumno
    );
  }

  function enviarLiquidaciones(e, l) {
    e.preventDefault();
    axios
      .post(
        "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/enviar_boletapagoweb.php?idcuota=" +
          idCuota +
          "&idalum=" +
          l.idalumno +
          "&cuota=" +
          nombreCuota
      )
      .then((response) => {
        console.log(response?.data);

        if (response?.data?.toString() === "") {
          Swal.fire({
            title: "El Correo ha sido enviado con EXITO",
            icon: "success",
            html: `${response.data}`,
            showCloseButton: true,
          });
        } else {
          Swal.fire({
            title: "Error al Enviar el Correo!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          });
        }
      });
  }

  function orden(e, col) {
    e.preventDefault();

    if (order === "ASC" && col === "fhliq") {
      const sorted = [...liquidaciones].sort((a, b) =>
        a[col] > b[col] ? 1 : -1
      );
      dispatch(cargarTablaLiquidaciones(sorted));
      setOrder("DESC");
    } else if (order === "ASC") {
      const sorted = [...liquidaciones].sort((a, b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
      );
      dispatch(cargarTablaLiquidaciones(sorted));
      setOrder("DESC");
    }

    if (order === "DESC" && col === "fhliq") {
      const sorted = [...liquidaciones].sort((a, b) =>
        a[col] < b[col] ? 1 : -1
      );
      dispatch(cargarTablaLiquidaciones(sorted));
      setOrder("ASC");
    } else if (order === "DESC") {
      const sorted = [...liquidaciones].sort((a, b) =>
        a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
      );
      dispatch(cargarTablaLiquidaciones(sorted));
      setOrder("ASC");
    }
  }

  return (
    <div style={{ height: "900px" }} class="table-responsive">
      <Table
        responsive
        striped
        hover
        bordered
        className="w-100 table-condensed text-center table"
        size="sm"
      >
        <thead className="py-5">
          <tr className="bg-light">
            <th className="col-3" onClick={(e) => orden(e, "alumno")}>
              Alumno
            </th>
            <th className="col-1" onClick={(e) => orden(e, "nivel")}>
              Nivel
            </th>
            <th className="col-1" onClick={(e) => orden(e, "nombre")}>
              Curso
            </th>
            <th
              className="col-1 text-nowrap"
              onClick={(e) => orden(e, "fhliq")}
            >
              Fecha LiquidaciÃ³n
            </th>
            <th className="col-1">AcciÃ³n</th>
            <th className="col-1">Imprimir</th>
            <th className="col-1 text-nowrap">Enviar Correo</th>
          </tr>
        </thead>
        <tbody>
          {liquidaciones &&
            liquidaciones.map((l) => (
              <tr key={l[2]}>
                <td className="align-middle">
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap text-left mb-0"
                  >
                    {l.alumno}
                  </p>
                </td>
                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap mb-0"
                  >
                    {l.nivel}
                  </p>
                </td>
                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap mb-0"
                  >
                    {l.nombre}
                  </p>
                </td>

                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap mb-0"
                  >
                    {l.fhliq}
                  </p>
                </td>

                <td className="px-5">
                  <div className="d-block">
                    <button
                      style={{ width: "70px" }}
                      className={
                        l.traspaso === "0"
                          ? "btn rounded btn-success btn-sm"
                          : "btn rounded btn-danger btn-sm"
                      }
                      onClick={(e) => {
                        handleClick(e, l);
                      }}
                    >
                      {l.traspaso === "0" ? "Liquidar" : "Revertir"}
                    </button>
                    <div
                      style={{ marginLeft: "80px" }}
                      id={`spinner ${l.idalumno}`}
                      className="mt-2 mx-3 spinner-border spinner-border-sm spinner position-absolute d-none"
                      role="status"
                    ></div>
                  </div>
                </td>

                <td>
                  <button
                    className={
                      l.traspaso === "0" ? "d-none" : "border-0 bg-transparent"
                    }
                    onClick={(e) => imprimirLiquidaciones(e, l)}
                  >
                    <img width={25} src={printer} alt=""></img>
                  </button>
                </td>

                <td>
                  <button
                    className={
                      l.traspaso === "0"
                        ? "d-none mx-4"
                        : "border-0 bg-transparent"
                    }
                    onClick={(e) => {
                      enviarLiquidaciones(e, l);
                    }}
                  >
                    <img width={25} src={mail} alt=""></img>
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </Table>
      <div>
        {liquidaciones.length === 0 ? (
          <div className="footer text-center">No se registra informaciÃ³n</div>
        ) : (
          ""
        )}
      </div>
    </div>
  );
}
