import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../../style/styles.css";
import arrowLeft from "../../utils/left-arrow.png";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { cargarCodificador } from "../../actions";

export default function Codificador() {
  const { id, idcodificador } = useParams();
  const navigation = useNavigate();
  const dispatch = useDispatch();

  let [input, setInput] = useState({
    id: "",
    descripcion: "",
    codigo: "",
    codigo1: "",
    habilitado: true,
  });

  useEffect(() => {
    if (id !== "0") {
      axios
        .get(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/codificadores/traerdatoscodificadores.php?id=" +
            id +
            "&idcodificador=" +
            idcodificador
        )
        .then((response) => {
          console.log(response.data);
          setInput({
            id: response.data.id,
            descripcion: response.data.descripcion,
            codigo: response.data.codigonum,
            codigo1: response.data.codigo1,
            habilitado: response.data.habilitado,
          });
          //dispatch(cargarCodificador(input));
        });
    }
  }, [dispatch]);

  function handleChange(e) {
    e.preventDefault();
    setInput((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  }

  function editarCodificador(e) {
    e.preventDefault();
    if (id === "0") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/grabardatosbecas.php?oper=" +
            "A" +
            "&idbeca=" +
            0 +
            "&nombre=" +
            input.nombre +
            "&tipo=" +
            input.tipo +
            "&valor=" +
            input.valor
        )
        .then((response) => {
          console.log(response.data);
          if (response.data === "No se puede completar la operación") {
            Swal.fire({
              title: "Error al crear Beca!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            }).then((result) => {
              if (result.isConfirmed) {
                navigation("/becas");
              }
            });
          }
          navigation("/becas");
        });
    } else {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/grabardatosbecas.php?oper=" +
            "M" +
            "&idbeca=" +
            id +
            "&nombre=" +
            input.nombre +
            "&tipo=" +
            input.tipo +
            "&valor=" +
            input.valor
        )
        .then((response) => {
          console.log(response.data);
        });
      navigation("/becas");
    }
    document.querySelector(".navbar").classList.remove("active-nav");
    document.querySelector(".contenedor").classList.remove("active-contenedor");
  }

  return (
    <React.Fragment>
      <div className="pr-5 pt-3 pl-3 pl-lg-5 ml-lg-5 contenedor">
        <div className="pt-5">
          <div>
            <h4 className="">CODIFICADORES</h4>
            <p className="text-secondary">
              Crear y gestionar los datos del Codificador
            </p>
          </div>
          <hr className="w-100 mx-0" />
          <div className="d-flex justify-content-between w-100">
            <h4>
              Informacion del Codificador{"  "}
              <strong className="ml-3 text-secondary font-weight-normal">
                {input.descripcion}
              </strong>
            </h4>
            <div>
              <button
                type="button"
                className="btn"
                id="id_cancelar"
                onClick={() => {
                  navigation("/codificadores");
                  document
                    .querySelector(".navbar")
                    .classList.remove("active-nav");
                  document
                    .querySelector(".contenedor")
                    .classList.remove("active-contenedor");
                }}
              >
                {" "}
                <img width="20px" src={arrowLeft} alt="arrow-left" />
                Listado Codificadores
              </button>
            </div>
          </div>
          <hr className="w-100 mx-0" />
          <p className="text-secondary">
            Gestionar la informacion del Codificador
          </p>
        </div>
        <nav>
          <ul class="nav nav-tabs" id="myTab" role="tablist">
            <li class="nav-item" role="presentation">
              <button
                class="nav-link active"
                id="datos-tab"
                data-bs-toggle="tab"
                data-bs-target="#datos"
                type="button"
                role="tab"
                aria-controls="datos"
                aria-selected="true"
                style={{ outline: 0 }}
              >
                Datos Básicos
              </button>
            </li>
          </ul>
        </nav>

        <div class="tab-content" id="myTabContent">
          <div
            class="tab-pane fade show active"
            id="datos"
            role="tabpanel"
            aria-labelledby="datos-tab"
          >
            {/* DATOS BASICOS */}
            <div className="w-100 mt-3">
              <form
                name="f_abm_docentes"
                id="id_abm_docentes"
                className=""
                onSubmit={(e) => editarCodificador(e)}
              >
                <div className="form-group row align-items-center">
                  <label
                    htmlFor="descripcion"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Descripción
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="descripcion"
                      name={"descripcion"}
                      value={input.descripcion}
                      onChange={(e) => handleChange(e)}
                      placeholder={"Ingrese Descripción"}
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="codigo"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Código
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="codigo"
                      name={"codigo"}
                      value={input.codigo}
                      onChange={(e) => handleChange(e)}
                      placeholder={"Ingrese Código"}
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="codigo1"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Código 1
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="codigo1"
                      name={"codigo1"}
                      value={input.codigo1}
                      onChange={(e) => handleChange(e)}
                      placeholder={"Ingrese Valor"}
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="habilitada"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Habilitada
                  </label>
                  <div className="col-sm-4 col-lg-3 d-flex flex-row">
                    <div class="form-check m-2">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        value=""
                        id="flexCheckDefault"
                      />
                      <label class="form-check-label" for="flexCheckDefault">
                        Si
                      </label>
                    </div>
                    <div class="form-check">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        value=""
                        id="flexCheckChecked"
                        checked
                      />
                      <label class="form-check-label" for="flexCheckChecked">
                        No
                      </label>
                    </div>
                    {/* <input
                      required
