import React, { useEffect, useMemo } from "react";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import {
  useTable,
  useSortBy,
  useBlockLayout,
  useResizeColumns,
} from "react-table";
import mail from "../../utils/mail.png";
import printer from "../../utils/printer.png";
import { useSticky } from "react-table-sticky";
import styled from "styled-components";
import { NavLink } from "react-router-dom";

export default function TablaLiquidacion({
  ciclo,
  idCuota,
  nombreCuota,
  idAlumno,
}) {
  const dispatch = useDispatch();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          (idCuota ? idCuota : 0) +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        if (response.data) {
          return dispatch(cargarTablaLiquidaciones(response.data));
        } else {
          return dispatch(cargarTablaLiquidaciones([]));
        }
      });
  }

  const Styles = styled.div`
    .tr:nth-child(even) {
      background-color: #f7f7f7;
    }
    .tr:nth-child(n):hover {
      background-color: #f2f2f2;
    }

    .table {
      border: 1px solid #ddd;
      max-height: 900px;
      font-size: 13px;

      .tr {
        :last-child {
          .td {
            border-bottom: 0;
          }
        }
      }

      .th {
        font-weight: bold;
      }

      .th,
      .td {
        padding: 5px;
        border-bottom: 1px solid #ddd;
        border-right: 1px solid #ddd;
        overflow: hidden;
        transition: ease;

        position: relative;
        :last-child {
          border-right: 0;
        }
      }

      .resizer {
        display: inline-block;
        width: 5px;
        height: 100%;
        position: absolute;
        right: 0;
        top: 0;
        transform: translateX(50%);
        z-index: 1;
        ${"" /* prevents from scrolling while dragging on touch devices */}
        touch-action:none;

        &.isResizing {
          background: black;
        }
      }

      &.sticky {
        overflow: scroll;
        .header,
        .th {
          position: sticky;
          z-index: 1;
          top: 0;
          box-shadow: 0px 3px 3px #ccc;
          background-color: white;
          height: 50px;
        }

        .body {
          z-index: 0;
        }

        [data-sticky-td] {
          position: sticky;
        }

        [data-sticky-last-left-td] {
          box-shadow: 2px 0px 3px #ccc;
        }

        [data-sticky-first-right-td] {
          box-shadow: -2px 0px 3px #ccc;
        }
      }
      .btn {
        font-size: 12px;
      }

      .footer {
        padding: 5px 0;
        background-color: #f2dede;
        color: #e62f4d;
      }
    }
  `;

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  const columns = useMemo(
    () => [
      {
        Header: "Alumno",
        accessor: "alumno",
        minWidth: 150,
        width: windowWidth - widthContenedor === 0 ? 265 : 265,
        maxWidth: 345,
        sticky: "top",
        Cell: ({ value }) => (
          <div
            style={{
              textAlign: "left",
              overflow: "hidden",
              whiteSpace: "nowrap",
            }}
          >
            {value}
          </div>
        ),
      },
      {
        Header: "Nivel",
        accessor: "nivel",
        minWidth: 100,
        width: windowWidth - widthContenedor === 0 ? 120 : 150,
        maxWidth: 229,
        sticky: "top",
      },
      {
        Header: "Curso",
        accessor: "nombre",
        minWidth: 100,
        width: windowWidth - widthContenedor === 0 ? 120 : 150,
        maxWidth: 229,
        sticky: "top",
      },
      {
        Header: "Fecha Liquidación",
        accessor: "fhliq",
        minWidth: 100,
        width: windowWidth - widthContenedor === 0 ? 130 : 150,
        maxWidth: 229,
        sticky: "top",
      },
      {
        Header: "Acción",
        accessor: "traspaso",
        minWidth: 100,
        width: windowWidth - widthContenedor === 0 ? 120 : 150,
        maxWidth: 229,
        sticky: "top",
        Cell: ({ cell }) => (
          <div>
            <button
              className={
                cell.value === "0"
                  ? "btn rounded btn-success btn-sm"
                  : "btn rounded btn-danger btn-sm"
              }
              value={cell}
              onClick={(e) => {
                handleClick(e, cell);
              }}
            >
              {cell.value === "0" ? "Liquidar" : "Revertir"}
            </button>
            <div
              id={"spinner " + cell.row.id}
              className="ml-3 mt-2 spinner-border spinner-border-sm spinner position-absolute d-none"
              role="status"
            >
              <span class="visually-hidden"></span>
            </div>
          </div>
        ),
      },
      {
        Header: "Imprimir",
        accessor: "imprimir",
        minWidth: 100,
        width: windowWidth - widthContenedor === 0 ? 106 : 150,
        maxWidth: 229,
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0" ? "d-none" : "border-0 bg-transparent"
            }
            onClick={(e) => imprimirLiquidaciones(e, cell)}
          >
            <img width={25} src={printer} alt=""></img>
          </button>
        ),
      },
      {
        Header: "Enviar Correo",
        accessor: "enviar",
        minWidth: 100,
        width: windowWidth - widthContenedor === 0 ? 106 : 150,
        maxWidth: 229,
        sticky: "top",
        Cell: ({ cell }) => (
          <button
            className={
              cell.value === "0" ? "d-none" : "border-0 bg-transparent"
            }
            onClick={(e) => {
              enviarLiquidaciones(e, cell);
            }}
          >
            <img width={25} src={mail} alt=""></img>
          </button>
        ),
      },
    ],
    [ciclo, idCuota, idAlumno, widthContenedor]
  );

  function handleClick(e, cell) {
    e.preventDefault();
    document
      .getElementById(`spinner ${cell.row.id}`)
      .classList.remove("d-none");
    console.log(document.getElementById(`${cell.row.id}`));
    if (cell.row.original.traspaso === "1") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/revertircuota.php?ciclo=" +
            ciclo +
            "&idcuota=" +
            idCuota +
            "&idcurso=" +
            cell.row.original.idcurso +
            "&idnivel=" +
            cell.row.original.idnivel +
            "&idalumno=" +
            cell.row.original.idalumno
        )
        .then((response) => {
          if (response.data !== "") {
            Swal.fire({
              title: "Error al Revertir Liquidación!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            });
          }
        });
    } else {
      axios.post(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/liquidarcuota.php?" +
          "idcuota=" +
          idCuota +
          "&idcurso=" +
          cell.row.original.idcurso +
          "&userid=1" +
          "&idalumno=" +
          cell.row.original.idalumno
      );
      cargarTabla();
    }
    cargarTabla();
  }

  function imprimirLiquidaciones(e, cell) {
    e.preventDefault();
    window.open(
      "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/imprimir_boletapago.php?" +
        "idcuota=" +
        idCuota +
        "&idalum=" +
        cell.row.original.idalumno
    );
  }

  function enviarLiquidaciones(e, cell) {
    e.preventDefault();
    axios
      .post(
        "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/enviar_boletapagoweb.php?idcuota=" +
          idCuota +
          "&idalum=" +
          cell.row.original.idalumno +
          "&cuota=" +
          nombreCuota
      )
      .then((response) => {
        console.log(response?.data);

        if (response?.data?.toString() === "") {
          Swal.fire({
            title: "El Correo ha sido enviado con EXITO",
            icon: "success",
            html: "prueba html",
            showCloseButton: true,
          });
        } else {
          Swal.fire({
            title: "Error al Enviar el Correo!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          });
        }
      });
  }

  const tableInstance = useTable(
    { columns, data: liquidaciones },
    useSortBy,
    useBlockLayout,
    useResizeColumns,
    useSticky
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    tableInstance;

  console.log(liquidaciones);
  return (
    <div
      style={{ height: "900px" }}
      className="overflow-scroll table-responsive"
    >
      <table className="w-100 table-striped table-hover table-condensed">
        <thead className="bg-transparent">
          <tr>
            <th className="col-6">Alumno</th>
            <th className="col">Nivel</th>
            <th className="col">Curso</th>
            <th className="col">Fecha Liquidación</th>
            <th className="col">Acción</th>
            <th className="col">Imprimir</th>
            <th className="col">Enviar Correo</th>
          </tr>
        </thead>
        <tbody>
          {liquidaciones &&
            liquidaciones.map((l) => (
              <tr style={{ cursor: "pointer" }} key={l[2]}>
                <td>
                  <div className="ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l[0].toUpperCase()}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l[1].toUpperCase()}
                    </p>
                  </div>
                </td>
                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l[1].toUpperCase()}
                    </p>
                  </div>
                </td>

                <td>
                  <div className=" ml-3">
                    <p
                      style={{ fontSize: "13px" }}
                      className="fw-bold mb-1 text-nowrap "
                    >
                      {l[1]}
                    </p>
                  </div>
                </td>

                <td className="">
                  <NavLink
                    to={"/conceptosgenerales/" + l[2]}
                    type="button"
                    className="ml-3 btn"
                    id={u[2]}
                    edit="true"
                    onClick={(e) => {
                      document
                        .querySelector(".navbar")
                        .classList.remove("active-nav");
                      document
                        .querySelector(".contenedor")
                        .classList.remove("active-contenedor");
                    }}
                  >
                    <img width="20px" alt="edit" src={edit} />
                  </NavLink>
                </td>
                {/* <td className="d-flex justify-content-center">
                  <button
                    className="btn mt-2 mt-lg-0"
                    onClick={(e) => {
                      Swal.fire({
                        title:
                          "Está seguro que desea eliminar el docente " +
                          u[0] +
                          "?",
                        icon: "question",
                        html: "Si se confirma se dejará de visualizar en todas las opciones que se encuentre vinculado.",
                        showCloseButton: true,
                      }).then((result) => {
                        if (result.isConfirmed) {
                          eliminarConcepto(u[2]);
                          Swal.fire("Eliminado!", "", "success");
                        }
                        navigation("/conceptosgenerales");
                      });
                    }}
                  >
                    <img width="20px" alt="edit" src={trash} />
                  </button>
                </td> */}
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
