import React, { useEffect, useMemo } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import { useTable } from "react-table";

export default function TablaLiquidacion({ ciclo, idCuota, idAlumno }) {
  const dispatch = useDispatch();
  const navigation = useNavigate();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    console.log(ciclo, idCuota, idAlumno);
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          idCuota +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        console.log(response.data);
        return dispatch(cargarTablaLiquidaciones(response.data));
      });
  }

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  const columns = useMemo(
    () => [
      {
        Header: "Alumno",
        accessor: "alumno",
      },
      {
        Header: "Nivel",
        accessor: "nivel",
      },
      {
        Header: "Curso",
        accessor: "nombre",
      },
      {
        Header: "Fecha Liquidaci贸n",
        accessor: "fhliq",
      },
      {
        Header: "Acci贸n",
        accessor: "traspaso",
        Cell: ({ cell }) => (
          <button value={cell.row.values.name} onClick={(e) => handleClick(e)}>
            {}
          </button>
        ),
      },
    ],
    []
  );

  function handleClick(e) {
    e.preventDefault();
    console.log(e);
  }
  const tableInstance = useTable({ columns, data: liquidaciones });

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    tableInstance;

  return (
    <div
      style={{ height: "900px" }}
      className="overflow-scroll table-responsive"
    >
      <table
        className="w-100 table-striped table-hover table-condensed"
        {...getTableProps()}
      >
        <thead className="bg-transparent">
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
