export function createUser(info){
    return {
        type: 'ADD_USER',
        payload: info
    }
}

export function modifyUser(info){
    return {
        type: 'MODIFY_USER',
        payload: info
    }
}

export function deleteUser(info){
    return {
        type: 'DELETE_USER',
        payload: info
    }
}
